import { initializeApp, getApps } from "firebase/app";
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from "firebase/auth";
import { getFirestore, setLogLevel } from "firebase/firestore";

// You may want to replace this config with your own Firebase project
const firebaseConfig = {
  apiKey: "AIzaSyC0v6QdHcNAD2bBp7IE1OdZwdD4jeePXQE", // Safe for frontend use
  authDomain: "naqt-quizbowl.firebaseapp.com",
  projectId: "naqt-quizbowl",
  storageBucket: "naqt-quizbowl.appspot.com",
  messagingSenderId: "1234567890",
  appId: "1:1234567890:web:abcdef123456"
};

let app = getApps().length ? getApps()[0] : initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
setLogLevel('debug');

export { app, auth, db, signInAnonymously, signInWithCustomToken, onAuthStateChanged };
